import React, { useState } from 'react';
import { Box, TextField, IconButton, FormControl, Select, MenuItem, InputLabel, Snackbar, Alert, Typography } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';

const SearchEvents = () => {
  const [searchTitle, setSearchTitle] = useState('');
  const [eventType, setEventType] = useState('');
  const [searchDescription, setSearchDescription] = useState('');
  const [error, setError] = useState('');
  const [isSnackbarOpen, setSnackbarOpen] = useState(false);
  const [events, setEvents] = useState([]);

  // 假设的静态选项数据，这里仅以事件类型为例
  const eventTypes = [
    { id: 'None', title: 'All Types' }, 
    { id: 'concert', title: 'Concert' },
    { id: 'conference', title: 'Conference' },
    { id: 'meeting', title: 'Meeting' },
    { id: 'webinar', title: 'Webinar' },
    // 添加更多类型...
  ];

  const handleSearch = async () => {
    // 构建查询参数
    const params = new URLSearchParams({
      title: searchTitle.trim(),
      type: eventType === 'None' ? '' : eventType, // 如果选中的是'None'，则不添加类型作为搜索条件
      description: searchDescription.trim()
    }).toString();

    try {
      const response = await fetch(`http://127.0.0.1:5000/events?${params}`, {
        method: 'GET',
        headers: {
          'Cache-Control': 'no-cache'
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      if (data.length === 0) { // 假设返回的是直接的事件数组
        setError('No related events found. Please try again with different search criteria.');
        setSnackbarOpen(true);
      } else {
        setEvents(data); // 直接更新事件列表
      }
    } catch (error) {
      console.error('Fetch error:', error);
      setError('Failed to load events. Please try again.');
      setSnackbarOpen(true);
    }
  };

  const handleCloseSnackbar = () => {
    setSnackbarOpen(false);
  };

  return (
    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, justifyContent: 'center', alignItems: 'center' }}>
      <Snackbar open={isSnackbarOpen} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>

      <TextField
        label="Title"
        variant="outlined"
        size="small"
        value={searchTitle}
        onChange={(e) => setSearchTitle(e.target.value)}
      />
      <FormControl size="small" sx={{ minWidth: 120 }}>
        <InputLabel>Event Type</InputLabel>
        <Select
          value={eventType}
          label="Event Type"
          onChange={(e) => setEventType(e.target.value)}
        >
          {eventTypes.map((type) => (
            <MenuItem key={type.id} value={type.id}>{type.title}</MenuItem>
          ))}
        </Select>
      </FormControl>
      <TextField
        label="Description"
        variant="outlined"
        size="small"
        value={searchDescription}
        onChange={(e) => setSearchDescription(e.target.value)}
      />
      <IconButton onClick={handleSearch} size="large">
        <SearchIcon />
      </IconButton>

      {/* 渲染搜索结果 */}
      {events.map((event) => (
        <Box key={event.id} sx={{ mt: 2 }}>
          <Typography variant="h6">{event.title}</Typography>
          <Typography variant="body2">{event.description}</Typography>
          {/* 根据需要添加更多细节 */}
        </Box>
      ))}
    </Box>
  );
};

export default SearchEvents;
