import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Box, Typography, CircularProgress, Card, CardContent, Avatar, ThemeProvider } from '@mui/material';
import { createTheme } from '@mui/material/styles';
import SearchEvents from '../components/SearchEvents'; // Ensure this is the correct path
import Navbar from '../components/Navbar';
import HeaderLogo from '../components/HeaderLogo';

const theme = createTheme({
    palette: {
        primary: {
            main: '#e66465',
        },
        secondary: {
            main: '#9198e5',
        },
    },
});

const MyAccount = () => {
    const [userDetails, setUserDetails] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    const handleSearch = (searchTerm) => {
        console.log("Search term:", searchTerm);
        // Implement search logic here, e.g., filter your data or make a new API call
    };

    useEffect(() => {
        const fetchUserDetails = async () => {
            setIsLoading(true);
            try {
                // Adjust the URL to your API endpoint for fetching user details
                const response = await axios.get('http://localhost:5005/user/auth/register', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`, // Ensure you're sending the authorization token if needed
                    },
                });
                setUserDetails(response.data);
            } catch (error) {
                setError('Failed to load user details. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchUserDetails();
    }, []);


    return (
        <ThemeProvider theme={theme}>
        <Box sx={{
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            background: `url(${process.env.PUBLIC_URL}/default_background.jpg), linear-gradient(to right, #e66465, #9198e5)`,
            backgroundSize: 'cover, cover',
            backgroundPosition: 'center, center',
            p: theme.spacing(2),
        }}>
            <Box sx={{ position: 'absolute', top: 10, display: 'flex', alignItems: 'center', width: '100%', justifyContent: 'space-around' }}></Box>
            <Box sx={{ position: 'absolute', top: 10, right: 10, display: 'flex' }}>
                <SearchEvents onSearch={handleSearch} />
                {/* <CreateNewEvent /> */}
                {/* <MyBookings /> */}
                {/* <HostProfile /> */}
                {/* <Logout /> */}
                <Navbar></Navbar>
            </Box>
            <HeaderLogo theme={theme} />
                {isLoading ? (
                    <CircularProgress />
                ) : error ? (
                    <Typography color="error">{error}</Typography>
                ) : (
                    <Card sx={{ maxWidth: 345, mt: 5 }}>
                        <CardContent>
                            <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                                <Avatar
                                    sx={{ width: 100, height: 100, mb: 2 }}
                                    src="https://pic2.zhimg.com/80/v2-e6caae14bcb1ef3901b3d8af41752501_1440w.webp" // Replace with userDetails.avatar if available
                                    alt="User Avatar"
                                />
                                <Typography gutterBottom variant="h5" component="div">
                                    {userDetails.name}
                                </Typography>
                                <Typography variant="body2" color="text.secondary">
                                    UserID: {userDetails.userId}<br />
                                    Email: {userDetails.email}<br />
                                    Card Number: {userDetails.cardNumber ? `**** **** **** ${userDetails.cardNumber.slice(-4)}` : 'N/A'}<br />
                                    Wallet Balance: ${userDetails.wallet}<br />
                                </Typography>
                            </Box>
                        </CardContent>
                    </Card>
                )}
            </Box>
        </ThemeProvider>
    );
};

export default MyAccount;
