import React, { useState } from 'react';
import { Box, TextField, IconButton, FormControl, Select, MenuItem, InputLabel } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';

const SearchEvents = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [eventType, setEventType] = useState('');
  const [eventDuration, setEventDuration] = useState('');
  const [sortOrder, setSortOrder] = useState('');

  // 假设的静态选项数据
  const eventTypes = [
    { id: 1, title: 'Music' },
    { id: 2, title: 'Art' },
    { id: 2, title: 'Concert' },
    // 添加更多类型...
  ];

  const eventDurations = [
    { value: '1', label: '1 day' },
    { value: '3', label: '2-3 days' },
    // 更多选项...
  ];

  const sortOptions = [
    { value: '0', label: 'Latest Event' },
    { value: '1', label: 'Highest Viewed Event' },
    // 更多选项...
  ];

  const handleSearch = () => {
    onSearch(searchTerm.trim(), eventType, eventDuration, sortOrder);
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, justifyContent: 'center', alignItems: 'center' }}>
      <TextField
        label="Search Events"
        variant="outlined"
        size="small"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        onKeyPress={handleKeyPress}
      />
      <FormControl size="small" sx={{ minWidth: 120 }}>
        <InputLabel>Event Type</InputLabel>
        <Select
          value={eventType}
          label="Event Type"
          onChange={(e) => setEventType(e.target.value)}
        >
          {eventTypes.map((type) => (
            <MenuItem key={type.id} value={type.id}>{type.title}</MenuItem>
          ))}
        </Select>
      </FormControl>
      <FormControl size="small" sx={{ minWidth: 120 }}>
        <InputLabel>Duration</InputLabel>
        <Select
          value={eventDuration}
          label="Duration"
          onChange={(e) => setEventDuration(e.target.value)}
        >
          {eventDurations.map((duration) => (
            <MenuItem key={duration.value} value={duration.value}>{duration.label}</MenuItem>
          ))}
        </Select>
      </FormControl>
      <FormControl size="small" sx={{ minWidth: 120 }}>
        <InputLabel>Sort</InputLabel>
        <Select
          value={sortOrder}
          label="Sort"
          onChange={(e) => setSortOrder(e.target.value)}
        >
          {sortOptions.map((option) => (
            <MenuItem key={option.value} value={option.value}>{option.label}</MenuItem>
          ))}
        </Select>
      </FormControl>
      <IconButton onClick={handleSearch} size="large">
        <SearchIcon />
      </IconButton>
    </Box>
  );
};

export default SearchEvents;
