import React from 'react';
import AllEvents from '../components/AllEvents';
import MyBookings from '../components/MyBookings'; // 假设 MyEvents 和 MyBookings 相同
import CreateNewEvent from '../components/CreateNewEvent';
import HostProfile from '../components/HostProfile'; // 假设这是 MyAccount
import Logout from '../components/Logout';
import MyAccount from '../components/MyAccount'

const Navbar = () => {
  // 假设identity存储了用户角色信息
  const identity = localStorage.getItem('identity');

  // 根据用户角色动态决定展示哪些按钮
  const renderButtonsBasedOnRole = () => {
    const buttonsForCustomer = [
      <AllEvents key="allEvents" />,
      <MyBookings key="myBookings" />, // 替换为你的MyEvents组件如果有
      <MyAccount key="myAccount" />, // 假设HostProfile是MyAccount
      <Logout key="logout" />
    ];

    const buttonsForHost = [
      <AllEvents key="allEvents" />,
      <MyBookings key="hostedEvents" />, // 你可能需要创建一个专门展示hosted events的组件
      <CreateNewEvent key="createNewEvent" />,
      <HostProfile key="hostprofile" />,
      <Logout key="logout" />
    ];

    return identity === 'customer' ? buttonsForCustomer : buttonsForHost;
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'space-around', padding: '20px 0' }}>
      {renderButtonsBasedOnRole()}
    </div>
  );
};

export default Navbar;
