import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Box, Typography, CircularProgress, Card, CardContent, Avatar } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Navbar from '../components/Navbar';
import HeaderLogo from '../components/HeaderLogo';

const theme = createTheme({
    palette: {
        primary: {
            main: '#e66465',
        },
        secondary: {
            main: '#9198e5',
        },
    },
});

const MyAccount = () => {
    const [userDetails, setUserDetails] = useState({});
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchUserDetails = async () => {
            setIsLoading(true);
            try {
                // Adjust the URL to your API endpoint for fetching user details
                const response = await axios.get('http://localhost:5005/user/auth/register', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`, // Ensure you're sending the authorization token if needed
                    },
                });
                setUserDetails(response.data);
            } catch (error) {
                setError('Failed to load user details. Please try again later.');
            } finally {
                setIsLoading(false);
            }
        };

        fetchUserDetails();
    }, []);

    return (
        <ThemeProvider theme={theme}>
            <Box sx={{
                minHeight: '100vh',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                background: `url(${process.env.PUBLIC_URL}/default_background.jpg), linear-gradient(to right, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
                backgroundSize: 'cover',
                p: theme.spacing(2),
            }}>
                <Navbar />
                <HeaderLogo theme={theme} />
                {isLoading ? (
                    <CircularProgress />
                ) : error ? (
                    <Typography color="error">{error}</Typography>
                ) : (
                    <Card sx={{ maxWidth: 345, mt: 5 }}>
                        <CardContent>
                            <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                                <Avatar
                                    sx={{ width: 100, height: 100, mb: 2 }}
                                    src="https://via.placeholder.com/150" // Replace with userDetails.avatar if available
                                    alt="User Avatar"
                                />
                                <Typography gutterBottom variant="h5" component="div">
                                    {userDetails.name}
                                </Typography>
                                <Typography variant="body2" color="text.secondary">
                                    UserID: {userDetails.userId}<br />
                                    Email: {userDetails.email}<br />
                                    Card Number: {userDetails.cardNumber ? `**** **** **** ${userDetails.cardNumber.slice(-4)}` : 'N/A'}<br />
                                    Wallet Balance: ${userDetails.wallet}<br />
                                </Typography>
                            </Box>
                        </CardContent>
                    </Card>
                )}
            </Box>
        </ThemeProvider>
    );
};

export default MyAccount;
