import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Avatar,
  List,
  ListItem,
  ListItemText,
} from '@mui/material';

const MyAccount = () => {
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(true);
    const [userDetails, setUserDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserDetails = async () => {
            try {
                const userId = localStorage.getItem('userId');
                const response = await axios.get(`http://localhost:5005/user/${userId}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`,
                    },
                });
                setUserDetails(response.data);
                setIsLoading(false);
            } catch (error) {
                setError('Failed to load user details. Please try again later.');
                setIsLoading(false);
            }
        };

        fetchUserDetails();
    }, [navigate]);

    return (
        <Box
            sx={{
                minHeight: '100vh',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                p: 2,
            }}
        >
            {isLoading ? (
                <CircularProgress />
            ) : error ? (
                <Typography color="error">{error}</Typography>
            ) : userDetails ? (
                <Paper elevation={3} sx={{ p: 4, width: '100%', maxWidth: '500px' }}>
                    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                    <Avatar
                        sx={{ width: 120, height: 120 }}
                        src={"https://pic2.zhimg.com/80/v2-e6caae14bcb1ef3901b3d8af41752501_1440w.webp"} // Fixed avatar image
                        alt="User Avatar"
                    />
                        <Typography variant="h6">{userDetails.name}</Typography>
                        <Typography color="textSecondary">{userDetails.email}</Typography>
                    </Box>
                    <List>
                        <ListItem>
                            <ListItemText primary="UserID" secondary={userDetails.userId} />
                        </ListItem>
                        <ListItem>
                            <ListItemText primary="Email" secondary={userDetails.email} />
                        </ListItem>
                        <ListItem>
                            <ListItemText primary="Card Number" secondary={`**** **** **** ${userDetails.cardNumber.slice(-4)}`} />
                        </ListItem>
                        <ListItem>
                            <ListItemText primary="Wallet Balance" secondary={`$${userDetails.wallet.toFixed(2)}`} />
                        </ListItem>
                        {/* Include other details as needed */}
                    </List>
                </Paper>
            ) : (
                <Typography variant="subtitle1">User not found.</Typography>
            )}
        </Box>
    );
};

export default MyAccount;
