import React, { useEffect, useState } from 'react';
import axios from 'axios';
// import { Card, CardContent, CardMedia, CircularProgress, Box } from '@mui/material';
import { createTheme } from '@mui/material/styles';
import Logout from '../components/Logout';
import CreateNewEvent from '../components/CreateNewEvent';
import MyEvents from '../components/MyEvents';
import HostProfile from '../components/HostProfile';
import {useNavigate} from "react-router-dom";
import { useParams } from 'react-router-dom';
import { ThemeProvider } from '@mui/material/styles';
import {
    Box,
    Card,
    CardContent,
    CardMedia,
    Typography,
    Button,
    Grid,
    CircularProgress,
    Backdrop,
    Container,
    Link,
} from '@mui/material';

const theme = createTheme({
    palette: {
        primary: {
            main: '#e66465',
        },
        secondary: {
            main: '#9198e5',
        },
    },
});

const EventDetails = () => {
    const { eventId } = useParams();
    // const [events, setEvents] = useState([]);
    const [eventsInfo, setEventsInfo] = useState({
        id: 'id',
        title: 'Title',
        address: 'Address',
        price: 0,
        thumbnail: '',
        organizerName: '',
        eventType: 'eventType',
        seatingCapacity: 100,
        duration: 0,
        startDate: '',
        endDate: '',
        description: '',
        youtubeUrl: '',
        orderdetails: '',
    });
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchEvents = async () => {
            setIsLoading(true);
            setError(null);
            try {
                // 定义请求配置对象，包括请求头
                const config = {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                };
                // 使用配置对象发起请求
                const response = await axios.get(`http://localhost:5005/events/${eventId}`, config);
                if (response.status === 200) {
                    const event = response.data;
                    const startDate = new Date(event.startDate);
                    const endDate = new Date(event.endDate);
                    const duration = (endDate - startDate) / (1000 * 60 * 60 * 24);
                    setEventsInfo({
                        ...eventsInfo,
                        id: event.id,
                        title: event.title,
                        address: event.address,
                        price: event.price,
                        thumbnail: event.thumbnail,
                        organizerName: event.organizerName,
                        eventType: event.eventType,
                        seatingCapacity: event.seatingCapacity,
                        duration: duration + 1,
                        startDate: event.startDate,
                        endDate: event.endDate,
                        description: event.description,
                        youtubeUrl: event.youtubeUrl,
                        orderdetails: event.orderdetails,
                    });
                    // console.log(event.id);
                    console.log(response.data);
                    console.log("response.data.orderDetails", response.data.orderdetails);
                }

            } catch (error) {
                console.error("There was an error fetching the events:", error);
                setError("Failed to load events. Please try again later.");
            } finally {
                setIsLoading(false);
            }
        };

        fetchEvents();
    }, []); // 这里的空数组表示这个effect在组件挂载时仅执行一次
    console.log(eventsInfo);

    return (
        <ThemeProvider theme={theme}>
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    minHeight: '100vh',
                    background: `linear-gradient(to right, #e66465, #9198e5), url(${process.env.PUBLIC_URL}/default_background.jpg)`,
                    backgroundSize: 'cover',
                    p: 3,
                }}
            >
                <Backdrop
                    sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
                    open={isLoading}
                >
                    <CircularProgress color="inherit" />
                </Backdrop>
                <Container maxWidth="md">
                    <Grid container spacing={3} justifyContent="center">
                        <Grid item xs={12}>
                            <Typography variant="h4" gutterBottom align="center" color="common.white">
                                Event Details
                            </Typography>
                        </Grid>
                        {error ? (
                            <Typography variant="h6" color="error" align="center">
                                {error}
                            </Typography>
                        ) : (
                            <Grid item xs={12} md={8} lg={6}>
                                <Card raised sx={{ maxWidth: 600, mx: "auto", boxShadow: "5px 5px 15px rgba(0,0,0,0.2)" }}>
                                    <CardMedia
                                        component="img"
                                        height="250"
                                        image={eventsInfo.thumbnail || `${process.env.PUBLIC_URL}/placeholder_image.jpg`}
                                        alt="event"
                                    />
                                    <CardContent>
                                        <Typography gutterBottom variant="h5" component="div">
                                            {eventsInfo.title}
                                        </Typography>
                                        <Typography variant="body1" color="text.secondary" sx={{ mb: 2 }}>
                                            {eventsInfo.description}
                                        </Typography>
                                        <Typography variant="body2" color="text.primary">
                                            <strong>Organizer:</strong> {eventsInfo.organizerName}
                                        </Typography>
                                        <Typography variant="body2" color="text.primary">
                                            <strong>Address:</strong> {eventsInfo.address}
                                        </Typography>
                                        <Typography variant="body2" color="text.primary">
                                            <strong>Price:</strong> ${eventsInfo.price}
                                        </Typography>
                                        <Typography variant="body2" color="text.primary">
                                            <strong>Event Type:</strong> {eventsInfo.eventType}
                                        </Typography>
                                        <Typography variant="body2" color="text.primary">
                                            <strong>Seating Capacity:</strong> {eventsInfo.seatingCapacity}
                                        </Typography>
                                        <Typography variant="body2" color="text.primary">
                                            <strong>Start Date:</strong> {new Date(eventsInfo.startDate).toLocaleDateString()}
                                        </Typography>
                                        <Typography variant="body2" color="text.primary">
                                            <strong>End Date:</strong> {new Date(eventsInfo.endDate).toLocaleDateString()}
                                        </Typography>
                                        <Typography variant="body2" color="text.primary">
                                            <strong>Duration:</strong> {eventsInfo.duration} Days
                                        </Typography>
                                        {eventsInfo.youtubeUrl && (
                                            <Typography variant="body2" color="text.primary" sx={{ mt: 2 }}>
                                                <Link href={eventsInfo.youtubeUrl} target="_blank" rel="noopener">
                                                    Watch Event Trailer
                                                </Link>
                                            </Typography>
                                        )}
                                    </CardContent>
                                </Card>
                            </Grid>
                        )}
                    </Grid>
                </Container>
            </Box>
        </ThemeProvider>
    );
};
export default EventDetails;
