import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Box,
  Typography,
  Avatar,
  Paper,
  List,
  ListItem,
  ListItemText,
  CircularProgress,
} from '@mui/material';

const MyAccount = () => {
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(true);
    const [userDetails, setUserDetails] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserDetails = async () => {
            try {
                // Example endpoint, update to your API's endpoint
                const response = await axios.get('http://localhost:5005/user/details', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`,
                    },
                });

                setUserDetails(response.data);
                setIsLoading(false);
            } catch (error) {
                console.error('Error fetching user details:', error);
                setError('Failed to load user details. Please try again later.');
                setIsLoading(false);
            }
        };

        fetchUserDetails();
    }, []);

    return (
        <Box sx={{
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            background: `url(${process.env.PUBLIC_URL}/default_background.jpg), linear-gradient(to right, #e66465, #9198e5)`,
            backgroundSize: 'cover',
            p: 4,
        }}>
            <Paper sx={{ width: '100%', maxWidth: 500, p: 2 }}>
                {isLoading ? (
                    <CircularProgress />
                ) : error ? (
                    <Typography color="error">{error}</Typography>
                ) : userDetails && (
                    <>
                        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                            {/* Replace with actual user avatar if available */}
                            <Avatar sx={{ width: 80, height: 80, mb: 2 }} />
                            <Typography variant="h5">{userDetails.Name}</Typography>
                            {/* You can add more user details here */}
                        </Box>
                        <List>
                            <ListItem divider>
                                <ListItemText primary="UserID" secondary={userDetails.userId} />
                            </ListItem>
                            <ListItem divider>
                                <ListItemText primary="Email" secondary={userDetails.email} />
                            </ListItem>
                            <ListItem divider>
                                <ListItemText primary="Card Number" secondary={`**** **** **** ${userDetails.cardNumber.slice(-4)}`} />
                            </ListItem>
                            <ListItem divider>
                                <ListItemText primary="Wallet Balance" secondary={`$${userDetails.wallet}`} />
                            </ListItem>
                            {/* More details can be added here */}
                        </List>
                    </>
                )}
            </Paper>
        </Box>
    );
};

export default MyAccount;
