import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Box, Typography, CircularProgress, Avatar } from '@mui/material';

const MyAccount = () => {
    const [userDetails, setUserDetails] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserDetails = async () => {
            setIsLoading(true);
            try {
                // Replace the userId with the actual id from local storage or context
                const userId = localStorage.getItem('userId');
                const response = await axios.get(`http://localhost:5005/user/details/${userId}`);
                setUserDetails(response.data);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchUserDetails();
    }, []);

    if (isLoading) return <CircularProgress />;
    if (error) return <Typography color="error">Error: {error}</Typography>;
    if (!userDetails) return null;

    return (
        <Box sx={{ p: 2 }}>
            <Typography variant="h4">My Account</Typography>
            <Avatar
                sx={{ width: 56, height: 56 }}
                alt="User Avatar"
                src={userDetails.avatar || 'path_to_default_avatar_if_not_exist.jpg'} // Provide a default image path here
            />
            <Typography variant="subtitle1">Name: {userDetails.name}</Typography>
            <Typography variant="subtitle1">Email: {userDetails.email}</Typography>
            <Typography variant="subtitle1">Card Number: {userDetails.cardNumber}</Typography>
            <Typography variant="subtitle1">Wallet Balance: {userDetails.wallet}</Typography>
            {/* Add more fields as needed */}
        </Box>
    );
};

export default MyAccount;
