import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { TextField, Button, Typography, Box, useTheme, IconButton } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import zxcvbn from 'zxcvbn';

const Registercustomer = () => {

    const [registerData, setRegisterData] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: '',
        cardNumber: '',
        cardCVC: '',
        cardExpirationDate: null,
    });

    const updateField = (e) => {
        setRegisterData({
            ...registerData,
            [e.target.name]: e.target.value,
        });
    };

    const handleBack = () => {
        navigate(-1);
    };

    const validateEmail = (email) => {
        return RegExp(/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@(([^<>()[\]\\.,;:\s@"]+\.)+[^<>()[\]\\.,;:\s@"]{2,})$/i).test(email.toLowerCase());
    };

    const validateCardNumber = (number) => {
        return /^\d{16}$/.test(number);
    };

    const validateCVC = (cvc) => {
        return /^\d{3,4}$/.test(cvc);
    };

    const handleExpirationDateChange = (e) => {
        let value = e.target.value;
        value = value.replace(/\D/g, ''); // Only digits
        if (value.length <= 2) {
            value = value;
        } else if (value.length > 2 && value.length <= 4) {
            value = `${value.slice(0, 2)}/${value.slice(2, 4)}`;
        }
        setRegisterData(prevState => ({
            ...prevState,
            cardExpirationDate: value
        }));
    };

    const registerUser = async (e) => {
        e.preventDefault();

        if (!validateEmail(registerData.email)) {
            alert("Please enter a valid email address.");
            return;
        }

        const passwordStrength = zxcvbn(registerData.password);
        if (passwordStrength.score < 2) {
            alert("Password is too weak. Try using a mix of letters, numbers, and symbols.");
            return;
        }

        if (registerData.password !== registerData.confirmPassword) {
            alert("Passwords do not match.");
            return;
        }

        if (registerData.userType === 'customer' && !validateCardNumber(registerData.cardNumber)) {
            alert("Please enter a valid 16-digit card number.");
            return;
        }

        if (registerData.userType === 'customer' && !validateCVC(registerData.cardCVC)) {
            alert("Please enter a valid CVC.");
            return;
        }

        // Construct your user info object as needed for your backend
        const userInfo = {
            userType: registerData.userType,
            name: registerData.name,
            companyName: registerData.companyName,
            email: registerData.email,
            password: registerData.password,
            cardNumber: registerData.cardNumber,
            cardCVC: registerData.cardCVC,
            cardExpirationDate: registerData.cardExpirationDate,
        };

        try {
            // Adjust URL and headers as needed for your backend setup
            const response = await axios.post('http://localhost:5000/api/register', userInfo);
            if (response.status === 200) {
                alert('Registration successful');
                navigate('/login'); // Redirect to login page after successful registration
            }
        } catch (error) {
            console.error('Registration failed:', error);
            alert('Registration failed. Please try again.');
        }
    };

    return (
        <Box sx={{
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            p: theme.spacing(3),
        }}>
            <IconButton onClick={handleBack} sx={{ position: 'absolute', top: 20, left: 20 }}>
                <ArrowBackIcon />
            </IconButton>
            <Typography variant="h4" gutterBottom>Create Your Account</Typography>
            <Box component="form" onSubmit={registerUser} noValidate sx={{ mt: 1, width: '100%', maxWidth: 360 }}>
                {/* Conditional rendering based on userType */}
                {registerData.userType === 'customer' && (
                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        id="name"
                        label="Name"
                        name="name"
                        autoComplete="name"
                        autoFocus
                        value={registerData.name}
                        onChange={updateField}
                    />
                )}
                {registerData.userType === 'host' && (
                    <TextField
                        margin="normal"
                        required
                        fullWidth
                        id="companyName"
                        label="Company Name"
                        name="companyName"
                        autoComplete="company-name"
                        value={registerData.companyName}
                        onChange={updateField}
                    />
                )}
                {/* Common fields for both user types */}
                <TextField
                    margin="normal"
                    required
                    fullWidth
                    id="email"
                    label="Email Address"
                    name="email"
                    autoComplete="email"
                    value={registerData.email}
                    onChange={updateField}
                />
                <TextField
                    margin="normal"
                    required
                    fullWidth
                    name="password"
                    label="Password"
                    type="password"
                    id="password"
                    autoComplete="new-password"
                    value={registerData.password}
                    onChange={updateField}
                />
                <TextField
                    margin="normal"
                    required
                    fullWidth
                    name="confirmPassword"
                    label="Confirm Password"
                    type="password"
                    id="confirmPassword"
                    autoComplete="new-password"
                    value={registerData.confirmPassword}
                    onChange={updateField}
                />
                {/* Conditional rendering for customer payment information */}
                {registerData.userType === 'customer' && (
                    <>
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            id="cardNumber"
                            label="Card Number"
                            name="cardNumber"
                            autoComplete="cc-number"
                            value={registerData.cardNumber}
                            onChange={updateField}
                            inputProps={{ maxLength: 16 }}
                        />
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            id="cardCVC"
                            label="CVC"
                            name="cardCVC"
                            autoComplete="cc-csc"
                            value={registerData.cardCVC}
                            onChange={updateField}
                            inputProps={{ maxLength: 4 }}
                        />
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            name="cardExpirationDate"
                            label="Expiration Date (MM/YY)"
                            value={registerData.cardExpirationDate}
                            onChange={handleExpirationDateChange}
                            inputProps={{ maxLength: 5 }}
                        />
                    </>
                )}
                <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    sx={{ mt: 3, mb: 2 }}
                >
                    Register
                </Button>
            </Box>
        </Box>
    );
};

export default Register;
