import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, CardContent, CardMedia, CircularProgress, Box } from '@mui/material';
import { createTheme } from '@mui/material/styles';
import Logout from '../components/Logout';
import CreateNewEvent from '../components/CreateNewEvent';
import MyEvents from '../components/MyEvents';
import HostProfile from '../components/HostProfile';
import {useNavigate} from "react-router-dom";
import { useParams } from 'react-router-dom';
import { Typography, Paper, Grid, Container } from '@mui/material';

const theme = createTheme({
    palette: {
        primary: {
            main: '#e66465',
        },
        secondary: {
            main: '#9198e5',
        },
    },
});

const EventDetails = () => {
    const { eventId } = useParams();
    // const [events, setEvents] = useState([]);
    const [eventsInfo, setEventsInfo] = useState({
        id: 'id',
        title: 'Title',
        address: 'Address',
        price: 0,
        thumbnail: '',
        organizerName: '',
        eventType: 'eventType',
        seatingCapacity: 100,
        duration: 0,
        startDate: '',
        endDate: '',
        description: '',
        youtubeUrl: '',
        orderdetails: '',
    });
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchEvents = async () => {
            setIsLoading(true);
            setError(null);
            try {
                // 定义请求配置对象，包括请求头
                const config = {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                };
                // 使用配置对象发起请求
                const response = await axios.get(`http://localhost:5005/events/${eventId}`, config);
                if (response.status === 200) {
                    const event = response.data;
                    const startDate = new Date(event.startDate);
                    const endDate = new Date(event.endDate);
                    const duration = (endDate - startDate) / (1000 * 60 * 60 * 24);
                    setEventsInfo({
                        ...eventsInfo,
                        id: event.id,
                        title: event.title,
                        address: event.address,
                        price: event.price,
                        thumbnail: event.thumbnail,
                        organizerName: event.organizerName,
                        eventType: event.eventType,
                        seatingCapacity: event.seatingCapacity,
                        duration: duration + 1,
                        startDate: event.startDate,
                        endDate: event.endDate,
                        description: event.description,
                        youtubeUrl: event.youtubeUrl,
                        orderdetails: event.orderdetails,
                    });
                    // console.log(event.id);
                    console.log(response.data);
                    console.log("response.data.orderDetails", response.data.orderdetails);
                }

            } catch (error) {
                console.error("There was an error fetching the events:", error);
                setError("Failed to load events. Please try again later.");
            } finally {
                setIsLoading(false);
            }
        };

        fetchEvents();
    }, []); // 这里的空数组表示这个effect在组件挂载时仅执行一次
    console.log(eventsInfo);

    return (
        <>
            <Box sx={{
                minHeight: '100vh',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center',
                background: `url(${process.env.PUBLIC_URL}/default_background.jpg), linear-gradient(to right, #e66465, #9198e5)`,
                backgroundSize: 'cover, cover',
                backgroundPosition: 'center, center',
                p: theme.spacing(2),
            }}>
                <Box sx={{ position: 'absolute', top: 10, right: 10, display: 'flex' }}>
                    <CreateNewEvent />
                    <MyEvents />
                    <HostProfile />
                    <Logout />
                </Box>
                <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', width: '100%', mb: theme.spacing(4) }}>
                    <Box component="img" src={`${process.env.PUBLIC_URL}/LogoImage.jpg`} sx={{ width: 150, height: 'auto', mb: 2 }} />
                    <Typography variant="h3" color="white" sx={{ fontWeight: 'bold', textAlign: 'center' }}>
                        Our Amazing Ticket Platform
                    </Typography>
                </Box>
                {isLoading ? (
                    <CircularProgress />
                ) : error ? (
                    <Typography color="error">{error}</Typography>
                ) : eventsInfo.length > 0 ? (
                    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '20px', width: '90%' }}>
                        <Typography variant="h4" gutterBottom>Upcoming Events</Typography>
                        <Paper elevation={3} sx={{ p: 2 }}>
                            <Typography variant="h4" component="h1" gutterBottom>
                                {event.title}
                            </Typography>
                            <Typography variant="subtitle1" gutterBottom>
                                Organizer: {event.organizerName}
                            </Typography>
                            <Typography variant="body1" gutterBottom>
                                Address: {event.address}
                            </Typography>
                            <Grid container spacing={2}>
                                <Grid item xs={12} sm={6}>
                                    <Typography variant="body2">
                                        Start Date: {event.startDate}
                                    </Typography>
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <Typography variant="body2">
                                        End Date: {event.endDate}
                                    </Typography>
                                </Grid>
                            </Grid>
                            <Typography variant="body2" gutterBottom>
                                Duration: {event.duration} days
                            </Typography>
                            <Typography variant="body2" gutterBottom>
                                Price: ${event.price}
                            </Typography>
                            <Typography variant="body2" gutterBottom>
                                Seats Available: {event.seatingCapacity}
                            </Typography>
                            <Typography variant="body1" paragraph>
                                Description: {event.description}
                            </Typography>
                            {event.youtubeUrl && (
                                <Typography variant="body1" paragraph>
                                    Watch on <a href={event.youtubeUrl}>YouTube</a>
                                </Typography>
                            )}
                            {event.thumbnail && (
                                <img src={event.thumbnail} alt={event.title} style={{ width: '100%', height: 'auto' }} />
                            )}
                        </Paper>
                    </div>
                ) : (
                    <Typography variant="subtitle1">No events found.</Typography>
                )}
            </Box>
        </>
    );

};

export default EventDetails;
