import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ThemeProvider, createTheme, Container, Box, Typography, Card, CardContent, CardActions, Grid, TextField, Button, IconButton, InputLabel, MenuItem, Select } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';


const theme = createTheme({
    palette: {
        primary: {
            main: '#e66465',
        },
        secondary: {
            main: '#9198e5',
        },
    },
});

const CreateNewEvent = () => {
    const navigate = useNavigate();
    const [formErrors, setFormErrors] = useState({});
    const [eventData, setEventData] = useState({
        title: '',
        address: '',
        price: '',
        thumbnail: '',
        eventType: '',
        duration: '',
        seatingCapacity: '',
        amenities: '',
        youtubeUrl: '',
        startDate: '',
        endDate: '',
        organizerName: '',
        description: '',
        registrationLink: '',
    });

    const validateField = (name, value) => {
        switch (name) {
            case 'title':
                if (!value || value.length < 3) {
                    return 'Title must be at least 3 characters.';
                }
                break;
            case 'address':
                if (!value) {
                    return 'Address is required.';
                }
                break;
            case 'price':
                if (value <= 0) {
                    return 'Price must be greater than 0.';
                }
                break;
            case 'eventType':
                if (!value) {
                    return 'Event type is required.';
                }
                break;
            case 'duration':
                if (value <= 0) {
                    return 'Duration must be a positive number.';
                }
                break;
            case 'seatingCapacity':
                if (value <= 0) {
                    return 'Seating capacity must be a positive number.';
                }
                break;
            case 'youtubeUrl':
                if (value && !/^https:\/\/www\.youtube\.com\/watch\?v=[\w-]+$/.test(value)) {
                    return 'Invalid YouTube URL.';
                }
                break;
            case 'startDate':
            case 'endDate':
                if (!value) {
                    return 'This field is required.';
                }
                break;
            default:
                return '';
        }
    };

    const updateField = (e) => {
        const { name, value } = e.target;
        const errorMessage = validateField(name, value);
        setEventData({ ...eventData, [name]: value });
        setFormErrors({ ...formErrors, [name]: errorMessage });
    };

    const handleBack = () => {
        navigate(-1);
    };

    // 用于处理表单提交的逻辑
    const handleSubmit = (e) => {
        e.preventDefault();
        // 这里添加将eventData提交到后端的逻辑
        console.log(eventData);
        // 根据实际情况进行调整
    };

    return (
        <ThemeProvider theme={theme}>
            <Box
                sx={{
                    backgroundImage: `url(${process.env.PUBLIC_URL}/default_background.jpg), linear-gradient(to right, #e66465, #9198e5)`,
                    backgroundSize: 'cover, cover',
                    backgroundPosition: 'center, center',
                    minHeight: '100vh',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                }}
            >
                <IconButton
                    onClick={handleBack}
                    size="large"
                    sx={{
                        position: 'absolute',
                        left: theme.spacing(2),
                        top: theme.spacing(2),
                        backgroundColor: 'white',
                        color: 'primary.main',
                        '&:hover': {
                            backgroundColor: 'primary.light',
                            color: 'white',
                        },
                        boxShadow: 3,
                    }}
                >
                    <ArrowBackIcon sx={{ fontSize: 28 }} />
                </IconButton>
                <Container maxWidth="md" sx={{ mb: 4, backgroundColor: 'rgba(255, 255, 255, 0.8)', borderRadius: '15px', p: 2, mt: 5 }}>
                    <Card raised sx={{ mt: 3 }}>
                        <CardContent>
                            <Typography variant="h5" textAlign="center" gutterBottom>
                                Host Your New Event
                            </Typography>
                            <Grid container spacing={2}>
                                <Grid item xs={12}>
                                    <TextField
                                        name="title"
                                        label="Event Title"
                                        fullWidth
                                        value={eventData.title}
                                        onChange={updateField}
                                        required
                                        error={!!formErrors.title}
                                        helperText={formErrors.title}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        name="address"
                                        label="Event Address"
                                        fullWidth
                                        value={eventData.address}
                                        onChange={updateField}
                                        required
                                        error={!!formErrors.address}
                                        helperText={formErrors.address}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        name="price"
                                        label="Ticket Price (per ticket)"
                                        fullWidth
                                        value={eventData.price}
                                        onChange={updateField}
                                        required
                                        type="number"
                                        error={!!formErrors.price}
                                        helperText={formErrors.price}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        name="eventType"
                                        label="Event Type"
                                        fullWidth
                                        value={eventData.eventType}
                                        onChange={updateField}
                                        required
                                        error={!!formErrors.eventType}
                                        helperText={formErrors.eventType}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        name="duration"
                                        label="Duration (hours)"
                                        fullWidth
                                        value={eventData.duration}
                                        onChange={updateField}
                                        required
                                        type="number"
                                        error={!!formErrors.duration}
                                        helperText={formErrors.duration}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        name="seatingCapacity"
                                        label="Seating Capacity"
                                        fullWidth
                                        value={eventData.seatingCapacity}
                                        onChange={updateField}
                                        required
                                        type="number"
                                        error={!!formErrors.seatingCapacity}
                                        helperText={formErrors.seatingCapacity}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        name="description"
                                        label="Event Description"
                                        fullWidth
                                        value={eventData.description}
                                        onChange={updateField}
                                        required
                                        multiline
                                        rows={4}
                                        error={!!formErrors.description}
                                        helperText={formErrors.description}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        name="youtubeUrl"
                                        label="YouTube Video URL"
                                        fullWidth
                                        value={eventData.youtubeUrl}
                                        onChange={updateField}
                                        error={!!formErrors.youtubeUrl}
                                        helperText={formErrors.youtubeUrl}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        name="registrationLink"
                                        label="Registration/Ticket Link"
                                        fullWidth
                                        value={eventData.registrationLink}
                                        onChange={updateField}
                                        error={!!formErrors.registrationLink}
                                        helperText={formErrors.registrationLink}
                                    />
                                </Grid>
                            </Grid>
                        </CardContent>
                        <CardActions>
                            <Box width="100%" display="flex" justifyContent="center">
                                <Button variant="contained" color="primary" onClick={handleSubmit}>
                                    Submit Event
                                </Button>
                            </Box>
                        </CardActions>
                    </Card>
                </Container>
            </Box>
        </ThemeProvider>
    );
};

export default CreateNewEvent;
